import { useEffect, useState } from "react";
import { Footer, Header } from "../../components";
import typeIcons from "../../utils/typeIcons";
import api from "../../services/api";
import {
  Container,
  Form,
  Input,
  Options,
  Save,
  TextArea,
  TypeIcons,
} from "./styles";
import { useParams } from "react-router-dom";
import { format } from "date-fns";

export const Task: React.FC = () => {
  const [lateCount, setLateCount] = useState<string>("");
  const [type, setType] = useState<any>();
  const [done, setDone] = useState<boolean>(false);
  const [title, setTitle] = useState<string>("");
  const [description, setDescription] = useState<string>("");
  const [date, setDate] = useState<any>();
  const [hour, setHour] = useState<any>();
  const [macddress, setMacddress] = useState<string>("22:22:22:22:22:22");

  const params = useParams();

  const lateVerify = async () => {
    await api
      .get(`/task/filter/late/22:22:22:22:22:22`)
      .then((response: any) => {
        setLateCount(response.data.length);
      });
  };

  const LoadTaskDetails = async () => {
    await api.get(`/task/${params.id}`).then((response: any) => {
      setType(response.data.type);
      setTitle(response.data.title);
      setDescription(response.data.description);
      setDate(format(new Date(response.data.when), "dd/MM/yyyy"));
      setHour(format(new Date(response.data.when), "HH:mm"));
    });
  };

  const Saving = async () => {
    await api
      .post("/task", {
        macddress,
        type,
        title,
        description,
        when: `${date}T${hour}:00.000`,
      })
      .then(() => alert("TAREFA CADASTRADA COM SUCESSO!"));
  };

  useEffect(() => {
    lateVerify();
    LoadTaskDetails();
  }, []);

  return (
    <Container>
      <Header lateCount={lateCount} />
      <Form>
        <TypeIcons>
          {typeIcons.map(
            (icon, index) =>
              index > 0 && (
                <button type="button" onClick={() => setType(index)}>
                  <img
                    src={icon}
                    alt="Tipo da Tarefa"
                    className={type && type != index && "inative"}
                  />
                </button>
              )
          )}
        </TypeIcons>

        <Input>
          <span>Título</span>
          <input
            type="text"
            placeholder="Título da tarefa..."
            onChange={(e) => setTitle(e.target.value)}
            value={title}
          />
        </Input>

        <TextArea>
          <span>Descrição</span>
          <textarea
            rows={5}
            placeholder="Detalhes da tarefa..."
            onChange={(e) => setDescription(e.target.value)}
            value={description}
          />
        </TextArea>

        <Input>
          <span>Data</span>
          <input
            type="date"
            placeholder="Título da tarefa..."
            onChange={(e) => setDate(e.target.value)}
            value={date}
          />
        </Input>

        <Input>
          <span>Hora</span>
          <input
            type="time"
            placeholder="Título da tarefa..."
            onChange={(e) => setHour(e.target.value)}
            value={hour}
          />
        </Input>

        <Options>
          <div>
            <input
              type="checkbox"
              checked={done}
              onChange={() => setDone(!done)}
            />
            <span>CONCLUÍDO</span>
          </div>
          <button type="button">EXCLUIR</button>
        </Options>

        <Save>
          <button type="button" onClick={Saving}>
            SALVAR
          </button>
        </Save>
      </Form>
      <Footer />
    </Container>
  );
};
