import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FilterCard, Footer, Header } from "../../components";
import api from "../../services/api";
import Qr from "qrcode.react";
import { Container, Content, QrCodeArea, ValidationCode } from "./styles";

export const QR: React.FC = () => {
  const [filterActived, setFilterActived] = useState<string>("all");
  const [lateCount, setLateCount] = useState<string>("");
  const [mac, setMac] = useState<string>();

  const lateVerify = async () => {
    await api
      .get(`/task/filter/${filterActived}/22:22:22:22:22:22`)
      .then((response: any) => {
        setLateCount(response.data.length);
      });
  };

  const SaveMac = async () => {
    await localStorage.setItem("@todo/macaddress");
  };

  const Notification = () => {
    setFilterActived("late");
  };

  useEffect(() => {
    lateVerify();
  }, [filterActived]);

  return (
    <Container>
      <Header clickNotification={Notification} />
      <Content>
        <h1>CAPTURE O QRCODE PELO APP</h1>
        <p>suas atividades serão sincronizadas com a do seu celular.</p>
        <QrCodeArea>
          <Qr value="getmacaddress" size={350} />
        </QrCodeArea>
        <ValidationCode>
          <span>Digite a numeração que apareceu no celular</span>
          <input
            type="text"
            onChange={(e) => setMac(e.target.value)}
            value={mac}
          />
          <button type="button">Sincronizar</button>
        </ValidationCode>
      </Content>
      <Footer />
    </Container>
  );
};
