import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 220px;
  box-shadow: -3px 1px 13px -2px rgba(0, 0, 0, 0.73);

  display: flex;
  align-items: center;
  justify-content: center;
`;

export const TopCard = styled.div``;

export const BottomCard = styled.div``;
