import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 70px;
  background: #20295f;
  border-bottom: 3px solid #ee6b25;

  display: flex;
`;

export const LeftSide = styled.div`
  width: 100%;
  height: 70px;
  display: flex;
  align-items: center;
  padding-left: 10px;

  img {
    width: 100px;
    height: 40px;
  }
`;

export const RightSide = styled.div`
  width: 100%;
  height: 70px;
  display: flex;
  align-items: center;
  justify-content: flex-end;

  a {
    color: #fff;
    font-weight: bold;
    text-decoration: none;
    margin: 0 10px;

    &:hover {
      color: #ee6b25;
    }
  }

  #notification {
    img {
      width: 25px;
      height: 30px;
    }

    span {
      background: #fff;
      color: #ee6b25;
    }
  }
`;
